package org.dsa.mediator.smithwaterman

import org.dsa.utils.DSAFunSuite

/**
  * Created by xubo on 2017/1/1.
  */
class SmithWatermanAffineSuite extends DSAFunSuite {

  test("SW 1") {
    val sw = new SmithWatermanAffine("GAC", "CCCAA","blosum",12,2)
    val score=sw.compute("AB", "AA",ssw.ScoreMatrix.scoreMatrix62,12,2)
    println(score)
  }

  test("SW 2 scoreMatrix50") {
    val sw = new SmithWatermanAffine("GAC", "CCCAA","blosum",12,2)
    val score=sw.compute("PTHIKWGD", "PTHIKWGD",ssw.ScoreMatrix.scoreMatrix50,12,2)
    println(score)
  }

  test("SW 3 scoreMatrix62") {
    val sw = new SmithWatermanAffine("GAC", "CCCAA","blosum",12,2)
    val score=sw.compute("PTHIKWGD", "PTHIKWGD",ssw.ScoreMatrix.scoreMatrix62,12,2)
    println(score._1)

    val tool=new SmithWatermanConstantGapScoringSuite
    tool.printlnArr2[Char](score._3)
  }

  test("SW 4 scoreMatrix62") {
    val sw = new SmithWatermanAffine("GAC", "CCCAA","blosum",12,2)
    val score=sw.compute("AAGCTA", "AAGACTAAAA",ssw.ScoreMatrix.scoreMatrix50,12,2)
    println(score._1)

    val tool=new SmithWatermanConstantGapScoringSuite
    tool.printlnArr2[Int](score._2)
    println()
    tool.printlnArr2[Char](score._3)
  }

  test("SW 6 scoreMatrix50") {
    val sw = new SmithWatermanAffine("GAC", "CCCAA","blosum",12,2)

    val query=""
    val ref=""
    val score=sw.compute(query, ref,ssw.ScoreMatrix.scoreMatrix50,12,2)
    println(score)
    val tool=new SmithWatermanConstantGapScoringSuite
    tool.printlnArr2[Int](score._2)
    println()
    tool.printlnArr2[Char](score._3)
  }
}
